$(document).ready(function(){$('a.lightbox').click(function(evt){evt.preventDefault();$('.figure img').attr('src',$(this).attr('href')).attr('alt',$(this).attr('alt'));$('.caption').html($(this).find('img').attr('alt'));positionBox();showBox()})});function showBox(){$('body').addClass('modalShowing');if(jQuery.browser.msie){$('#modalOverlay, .modal').show();if($.browser.msie&&$.browser.version=='6.0'){$('#modalOverlay').css("filter","alpha(opacity=33)").css('height',$(document).height())}}else{$('#modalOverlay, .modal').fadeIn("fast")}$('#modalOverlay, .buttonClose').bind('click',function(evt){evt.preventDefault();closeBox()});$('html').bind("keyup",function(evt){if(evt.keyCode==27||evt.keyCode==88){closeBox()}})}function closeBox(){$('.modal').bind('click',function(evt){evt.stopPropagation()});if(jQuery.browser.msie){$('.modal').hide();$('#modalOverlay').hide()}else{$('#modalOverlay, .modal').fadeOut("fast")}}function positionBox(){var oElement=$('.modal');var oWindow=$(window);var offsetLeft=parseInt((oWindow.width()-oElement.width())/2);var offsetTop=parseInt((oWindow.height()-oElement.height())/2);var modalWidth=parseInt(oElement.width());var modalHeight=parseInt(oElement.height());oElement.css('left',offsetLeft).css('top',offsetTop).css('width',modalWidth).css('height',modalHeight).css('position','fixed');if($.browser.msie&&$.browser.version=='6.0'){oElement.css('position','absolute').css('top',parseInt(offsetTop+$(window).scrollTop()))}}